package com.qixin.teamwork.controller.image;
import java.io.File; 
import java.io.FileInputStream;
import java.nio.channels.FileChannel;
import java.util.HashMap;
import java.util.Map;
import java.util.Properties;

import org.framework.utils.BASE64;
import org.framework.utils.StrUtils;
import org.framework.utils.img.FileUtil;
import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import com.qixin.teamwork.biz.base.APIErrorMap;

/**
 *  base64字符串转化成图片服务类
* @author xiehuilin
* @Description: TODO( base64字符串转化成图片) 
* @date 2016年4月11日 上午11:04:12
 */
@RestController
@CrossOrigin(origins="*")
@RequestMapping("/image")
public class GenerateImageController {
	/**
	 *  base64字符串转化成图片
	*@author xiehuilin
	* @param @param imgStr  base64字符串
	* @param @return   
	* @date 2016年4月11日 上午10:49:45 
	* @return String  返回类型
	 */
	@RequestMapping(path="/generateImage",method=RequestMethod.POST)
	public Map<String, Object>  generateImage(@RequestParam String imgStr) { // 对字节数组字符串进行Base64解码并生成图片
	//public  Map<String, Object> generateImage() {
		Map<String,Object> reslutMap=new HashMap<String, Object>();
		String errorCode="";
		String msg="success";
		String state="1";  // 1 成功  0 失败
		//String imgStr="";
		String imgUrl=""; 
		reslutMap= this.checkAndParse(imgStr);
		boolean isNull=reslutMap.size()>0?false:true;
		if(isNull){
			try {
			// Base64解码
			byte[] b = BASE64.getInstance().decode(imgStr);
			for (int i = 0; i < b.length; ++i) {
			if (b[i] < 0) {// 调整异常数据
			    b[i] += 256;
			  }
			}
			String  fileType = FileUtil.getFileSuffix(b);//图片类型
			Properties props = new Properties(); 
			props.load(Thread.currentThread().getContextClassLoader().getResourceAsStream("activity.properties"));
			String model = props.getProperty("uploadImage");
			System.out.println(model);
			String fileurl = StrUtils.getFormatNow("yyyyMM/dd/");
			String filepath = model+ fileurl;
			// 100 随机
			int rm = (int) (Math.random() * 10);
			// 保存文件名称
			String filename = new StringBuffer("").append(rm).append(System.currentTimeMillis()).append("_").append(rm).append(".").append(fileType).toString();
			 imgUrl =fileurl + filename;
			 FileUtil.saveFile(b, filepath, filename);
			 File f= new File(filepath + filename);
			 FileChannel fc= null;  
			 long size=0l;//图片大小
			 long MAX_SIZE=2097152L;
			 if (f.exists() && f.isFile()){  
		            fc= new FileInputStream(f).getChannel();
		            size= fc.size();//获取图片的大小
		            System.out.println(size);
		        }
			 //图片大于2M
			 if(size>=MAX_SIZE){
				 	errorCode=APIErrorMap.errorMap.get("52");
					msg="error";
					state="0";
				 	new File(filepath + filename).delete();//删除垃圾图片
				 	reslutMap.put("errorCode", errorCode);
					reslutMap.put("msg", msg);
					reslutMap.put("state", state);
				 return reslutMap;
			 	}
			} catch (Exception e) {
				 msg="error";
				 state="0";  // 1 成功  0 失败
				 return reslutMap;
			}
			reslutMap.put("errorCode", errorCode);
			reslutMap.put("msg", msg);
			reslutMap.put("state", state);
			reslutMap.put("imgUrl", imgUrl);
		}
		return reslutMap;
}
	/**
	 * 校验客户端请求参数合法性
	 * @param jsonStr
	 * @param map
	 * @param errorCode
	 * @return
	 */
	private Map<String,Object> checkAndParse(String imgStr) {
		Map<String, Object>  reslutMap=new HashMap<String, Object>();
		String errorCode="";
		String msg="success";
		String state="1";  // 1 成功  0 失败
		if(StrUtils.isEmpty(imgStr)){
			errorCode=APIErrorMap.errorMap.get("51");
			msg="error";
			state="0";
			reslutMap.put("errorCode", errorCode);
			reslutMap.put("msg", msg);
			reslutMap.put("state", state);
		}
		return reslutMap;
	}
		
}
